import React, { useState, useEffect, useContext } from "react";
import axios from "axios";
import { AuthContext } from "../context/AuthContext";
import { ProgressContext } from "../context/ProgressContext";
import Navbar from "../components/Navbar";
import TopicCard from "../components/TopicCard";
import LoadingSpinner from "../components/LoadingSpinner";
import { TrendingUp, Target, Award, Flame, Search, Filter } from "lucide-react";

const API_URL = "http://localhost:5000/api";

const Dashboard = () => {
  const { user } = useContext(AuthContext);
  const { progress, getOverallProgress } = useContext(ProgressContext);
  const [topics, setTopics] = useState([]);
  const [loading, setLoading] = useState(true);
  const [searchQuery, setSearchQuery] = useState("");
  const [filterDifficulty, setFilterDifficulty] = useState("All");

  useEffect(() => {
    loadTopics();
  }, []);

  const loadTopics = async () => {
    try {
      const res = await axios.get(`${API_URL}/topics`);
      setTopics(res.data);
      setLoading(false);
    } catch (err) {
      console.error("Error loading topics:", err);
      setLoading(false);
    }
  };

  // Calculate statistics
  const totalProblems = topics.reduce(
    (sum, topic) => sum + topic.problems.length,
    0
  );
  const completedProblems = progress.completedProblems.length;
  const overallProgress = getOverallProgress(totalProblems);

  // Filter topics
  const filteredTopics = topics.filter((topic) =>
    topic.title.toLowerCase().includes(searchQuery.toLowerCase())
  );

  if (loading) {
    return <LoadingSpinner message="Loading your dashboard..." />;
  }

  return (
    <div className="min-h-screen">
      <Navbar />

      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        {/* Welcome Section */}
        <div className="mb-8 animate-fade-in">
          <h1 className="text-4xl font-bold text-gray-100 mb-2">
            Welcome back,{" "}
            <span className="bg-gradient-to-r from-primary-400 to-purple-400 bg-clip-text text-opaque">
              {user?.name}!
            </span>{" "}
            üëã
          </h1>
          <p className="text-gray-400 text-lg">
            Track your progress and continue mastering DSA
          </p>
        </div>

        {/* Stats Grid */}
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6 mb-8">
          {/* Stat Card 1 */}
          <div className="glass-effect p-6 rounded-2xl border border-white/10 card-hover">
            <div className="flex items-center justify-between mb-4">
              <div className="w-12 h-12 bg-primary-500/20 rounded-xl flex items-center justify-center">
                <TrendingUp className="w-6 h-6 text-primary-400" />
              </div>
              <span className="text-2xl">üìà</span>
            </div>
            <h3 className="text-gray-400 text-sm mb-1">Overall Progress</h3>
            <p className="text-3xl font-bold text-gray-100">
              {overallProgress}%
            </p>
            <div className="w-full bg-gray-700/50 rounded-full h-2 mt-3">
              <div
                className="h-full bg-gradient-to-r from-primary-500 to-purple-600 rounded-full transition-all duration-500"
                style={{ width: `${overallProgress}%` }}
              ></div>
            </div>
          </div>

          {/* Stat Card 2 */}
          <div className="glass-effect p-6 rounded-2xl border border-white/10 card-hover">
            <div className="flex items-center justify-between mb-4">
              <div className="w-12 h-12 bg-green-500/20 rounded-xl flex items-center justify-center">
                <Target className="w-6 h-6 text-green-400" />
              </div>
              <span className="text-2xl">‚úÖ</span>
            </div>
            <h3 className="text-gray-400 text-sm mb-1">Problems Solved</h3>
            <p className="text-3xl font-bold text-gray-100">
              {completedProblems}
            </p>
            <p className="text-sm text-gray-500 mt-1">
              of {totalProblems} total
            </p>
          </div>

          {/* Stat Card 3 */}
          <div className="glass-effect p-6 rounded-2xl border border-white/10 card-hover">
            <div className="flex items-center justify-between mb-4">
              <div className="w-12 h-12 bg-yellow-500/20 rounded-xl flex items-center justify-center">
                <Flame className="w-6 h-6 text-yellow-400" />
              </div>
              <span className="text-2xl">üî•</span>
            </div>
            <h3 className="text-gray-400 text-sm mb-1">Current Streak</h3>
            <p className="text-3xl font-bold text-gray-100">7 days</p>
            <p className="text-sm text-gray-500 mt-1">Keep it up!</p>
          </div>

          {/* Stat Card 4 */}
          <div className="glass-effect p-6 rounded-2xl border border-white/10 card-hover">
            <div className="flex items-center justify-between mb-4">
              <div className="w-12 h-12 bg-purple-500/20 rounded-xl flex items-center justify-center">
                <Award className="w-6 h-6 text-purple-400" />
              </div>
              <span className="text-2xl">üèÜ</span>
            </div>
            <h3 className="text-gray-400 text-sm mb-1">Topics Completed</h3>
            <p className="text-3xl font-bold text-gray-100">
              {
                topics.filter(
                  (t) => getOverallProgress(t.problems.length) === 100
                ).length
              }
            </p>
            <p className="text-sm text-gray-500 mt-1">
              of {topics.length} topics
            </p>
          </div>
        </div>

        {/* Search & Filter */}
        <div className="flex flex-col sm:flex-row gap-4 mb-6">
          <div className="flex-1 relative">
            <Search className="absolute left-3 top-1/2 -translate-y-1/2 w-5 h-5 text-gray-400" />
            <input
              type="text"
              placeholder="Search topics..."
              value={searchQuery}
              onChange={(e) => setSearchQuery(e.target.value)}
              className="w-full pl-11 pr-4 py-3 bg-white/5 border border-white/10 rounded-lg focus:outline-none focus:ring-2 focus:ring-primary-500 text-gray-100 placeholder-gray-500"
            />
          </div>
        </div>

        {/* Topics Grid */}
        <div className="mb-6">
          <h2 className="text-2xl font-bold text-gray-100 mb-6 flex items-center">
            <span className="w-1 h-8 bg-gradient-to-b from-primary-500 to-purple-600 rounded-full mr-3"></span>
            Learning Path
          </h2>

          {filteredTopics.length === 0 ? (
            <div className="text-center py-12">
              <p className="text-gray-400">
                No topics found matching "{searchQuery}"
              </p>
            </div>
          ) : (
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
              {filteredTopics.map((topic, index) => (
                <div
                  key={topic._id}
                  className="animate-slide-in"
                  style={{ animationDelay: `${index * 0.1}s` }}
                >
                  <TopicCard topic={topic} />
                </div>
              ))}
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
