import React, { useState, useEffect, useContext } from "react";
import { useParams, useNavigate } from "react-router-dom";
import axios from "axios";
import { ProgressContext } from "../context/ProgressContext";
import Navbar from "../components/Navbar";
import ProblemCard from "../components/ProblemCard";
import LoadingSpinner from "../components/LoadingSpinner";
import {
  ArrowLeft,
  CheckCircle2,
  Filter,
  LayoutGrid,
  Link as LinkIcon,
  GitBranch,
} from "lucide-react";

const API_URL = "http://localhost:5000/api";

const iconMap = {
  "layout-grid": LayoutGrid,
  link: LinkIcon,
  "git-branch": GitBranch,
};

const TopicDetail = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const { getTopicProgress } = useContext(ProgressContext);

  const [topic, setTopic] = useState(null);
  const [loading, setLoading] = useState(true);
  const [difficultyFilter, setDifficultyFilter] = useState("All");

  useEffect(() => {
    const loadTopic = async () => {
      try {
        const res = await axios.get(`${API_URL}/topics/${id}`);
        setTopic(res.data);
      } catch (err) {
        console.error("Error loading topic:", err);
      } finally {
        setLoading(false);
      }
    };

    loadTopic();
  }, [id]);

  if (loading) {
    return <LoadingSpinner message="Loading topic..." />;
  }

  if (!topic) {
    return (
      <div className="min-h-screen flex items-center justify-center">
        <div className="text-center">
          <p className="text-gray-400 mb-4">Topic not found</p>
          <button
            onClick={() => navigate("/dashboard")}
            className="btn-primary"
          >
            Back to Dashboard
          </button>
        </div>
      </div>
    );
  }

  const Icon = iconMap[topic.icon] || LayoutGrid;
  const progress = getTopicProgress(topic._id, topic.problems.length);

  // Filter problems
  const filteredProblems =
    difficultyFilter === "All"
      ? topic.problems
      : topic.problems.filter((p) => p.difficulty === difficultyFilter);

  return (
    <div className="min-h-screen">
      <Navbar />

      <div className="max-w-6xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        {/* Back Button */}
        <button
          onClick={() => navigate("/dashboard")}
          className="flex items-center space-x-2 text-gray-400 hover:text-primary-400 mb-6 transition-colors"
        >
          <ArrowLeft className="w-5 h-5" />
          <span>Back to Dashboard</span>
        </button>

        {/* Topic Header */}
        <div className="glass-effect p-8 rounded-2xl mb-8 border border-white/10">
          <div className="flex items-start justify-between flex-wrap gap-4">
            <div className="flex items-start space-x-4">
              <div className="w-16 h-16 bg-gradient-to-br from-primary-500 to-purple-600 rounded-2xl flex items-center justify-center shadow-xl">
                <Icon className="w-9 h-9 text-white" />
              </div>
              <div>
                <h1 className="text-3xl font-bold text-gray-100 mb-2">
                  {topic.title}
                </h1>
                <p className="text-gray-400 mb-4">{topic.description}</p>
                <div className="flex items-center space-x-4 text-sm">
                  <span className="text-gray-400">
                    <span className="font-semibold text-gray-300">
                      {topic.problems.length}
                    </span>{" "}
                    Problems
                  </span>
                  <span className="text-gray-400">â€¢</span>
                  <span className="text-primary-400 font-semibold">
                    {progress}% Complete
                  </span>
                </div>
              </div>
            </div>

            {/* Completion Badge */}
            {progress === 100 && (
              <div className="flex items-center space-x-2 bg-green-500/20 px-4 py-2 rounded-full border border-green-500/30">
                <CheckCircle2 className="w-5 h-5 text-green-400" />
                <span className="text-green-400 font-semibold">Completed!</span>
              </div>
            )}
          </div>

          {/* Progress Bar */}
          <div className="mt-6">
            <div className="flex items-center justify-between mb-2">
              <span className="text-sm text-gray-400">Progress</span>
              <span className="text-sm font-semibold text-gray-300">
                {progress}%
              </span>
            </div>
            <div className="w-full bg-gray-700/50 rounded-full h-3 overflow-hidden">
              <div
                className="h-full bg-gradient-to-r from-primary-500 to-purple-600 rounded-full transition-all duration-500 relative"
                style={{ width: `${progress}%` }}
              >
                <div className="absolute inset-0 bg-white/20 animate-pulse"></div>
              </div>
            </div>
          </div>
        </div>

        {/* Difficulty Filter */}
        <div className="flex items-center space-x-3 mb-6">
          <Filter className="w-5 h-5 text-gray-400" />
          <span className="text-sm text-gray-400">Filter by difficulty:</span>
          {["All", "Easy", "Medium", "Hard"].map((difficulty) => (
            <button
              key={difficulty}
              onClick={() => setDifficultyFilter(difficulty)}
              className={`px-4 py-2 rounded-lg text-sm font-medium transition-all ${
                difficultyFilter === difficulty
                  ? "bg-primary-500 text-white"
                  : "bg-white/5 text-gray-400 hover:bg-white/10"
              }`}
            >
              {difficulty}
            </button>
          ))}
        </div>

        {/* Problems List */}
        <div className="space-y-4">
          {filteredProblems.length === 0 ? (
            <div className="glass-effect p-12 rounded-2xl text-center">
              <p className="text-gray-400">
                No problems found for this difficulty level
              </p>
            </div>
          ) : (
            filteredProblems.map((problem, index) => (
              <div
                key={problem._id}
                className="animate-slide-in"
                style={{ animationDelay: `${index * 0.05}s` }}
              >
                <ProblemCard
                  problem={problem}
                  topicId={topic._id}
                  index={index}
                />
              </div>
            ))
          )}
        </div>
      </div>
    </div>
  );
};

export default TopicDetail;
