import React, { useContext } from "react";
import { useNavigate } from "react-router-dom";
import { ProgressContext } from "../context/ProgressContext";
import {
  ChevronRight,
  CheckCircle2,
  LayoutGrid,
  Link as LinkIcon,
  GitBranch,
  Binary,
  Layers,
  Boxes,
} from "lucide-react";

const iconMap = {
  "layout-grid": LayoutGrid,
  link: LinkIcon,
  "git-branch": GitBranch,
  binary: Binary,
  layers: Layers,
  boxes: Boxes,
};

const TopicCard = ({ topic }) => {
  const navigate = useNavigate();
  const { getTopicProgress } = useContext(ProgressContext);

  const Icon = iconMap[topic.icon] || LayoutGrid;
  const progress = getTopicProgress(topic._id, topic.problems.length);
  const isCompleted = progress === 100;

  return (
    <div
      onClick={() => navigate(`/topic/${topic._id}`)}
      className="glass-effect p-6 rounded-2xl cursor-pointer card-hover group relative overflow-hidden"
    >
      {/* Background Gradient */}
      <div className="absolute inset-0 bg-gradient-to-br from-primary-500/10 to-purple-600/10 opacity-0 group-hover:opacity-100 transition-opacity duration-300"></div>

      <div className="relative z-10">
        {/* Header */}
        <div className="flex items-start justify-between mb-4">
          <div className="w-14 h-14 bg-gradient-to-br from-primary-500 to-purple-600 rounded-xl flex items-center justify-center shadow-lg group-hover:scale-110 transition-transform duration-300">
            <Icon className="w-7 h-7 text-white" />
          </div>

          {isCompleted && (
            <div className="bg-green-500/20 px-3 py-1 rounded-full flex items-center space-x-1 border border-green-500/30">
              <CheckCircle2 className="w-4 h-4 text-green-400" />
              <span className="text-xs font-semibold text-green-400">
                Completed
              </span>
            </div>
          )}
        </div>

        {/* Title & Description */}
        <h3 className="text-xl font-bold text-gray-100 mb-2 group-hover:text-primary-400 transition-colors">
          {topic.title}
        </h3>
        <p className="text-sm text-gray-400 mb-4 line-clamp-2">
          {topic.description}
        </p>

        {/* Stats */}
        <div className="flex items-center justify-between mb-4">
          <div className="text-sm text-gray-400">
            <span className="font-semibold text-gray-300">
              {topic.problems.length}
            </span>{" "}
            Problems
          </div>
          <div className="text-sm font-semibold text-primary-400">
            {progress}% Complete
          </div>
        </div>

        {/* Progress Bar */}
        <div className="w-full bg-gray-700/50 rounded-full h-2 mb-4 overflow-hidden">
          <div
            className="h-full bg-gradient-to-r from-primary-500 to-purple-600 rounded-full transition-all duration-500 ease-out"
            style={{ width: `${progress}%` }}
          ></div>
        </div>

        {/* Action Button */}
        <button className="w-full flex items-center justify-between px-4 py-2 bg-white/5 hover:bg-white/10 rounded-lg transition-all duration-300 group/btn">
          <span className="text-sm font-medium text-gray-300">
            Start Learning
          </span>
          <ChevronRight className="w-5 h-5 text-gray-400 group-hover/btn:translate-x-1 transition-transform" />
        </button>
      </div>
    </div>
  );
};

export default TopicCard;
